import React, {useState} from 'react';

// import { NavLink, Link } from 'react-router-dom';
// import { useSelector } from 'react-redux';
import './NewSplash.css'
import one from './images/1.png'
import two from './images/2.png'
import three from './images/3.png'
import four from './images/4.png'
import five from './images/5.png'
import six from './images/6.png'
import seven from './images/7.png'
import eight from './images/8.png'
import nine from './images/9.png'
import ten from './images/10.png'
import eleven from './images/11.png'
import twelve from './images/12.png'

function NewSplash({ isLoaded }) {
    const [mSpeed, setMspeed] = useState('')
    const [ySpeed, setYspeed] = useState(0)
    const [xSpeed, setXspeed] = useState(0)


    function parallax(e){
        const imgs = document.querySelectorAll(['.item'])
        console.log(imgs)
        imgs.forEach(item => {
            const speed = item.getAttribute('dataspeed')

            const x = (window.innerWidth - e.pageX*speed)/100
            const y = (window.innerHeight - e.pageY*speed)/100

            setMspeed(`translate(${x}px, ${y}px)`)
            setXspeed(x)
            setYspeed(y)
        })
    }



    return (
        <div className='bg-wrap' onMouseMove={parallax}>
            <img src={one} dataspeed='-1' className='item' alt='bg-img' style={{transform: `translate(${xSpeed * 2}px, ${ySpeed * 2}px)`}}></img>
            <img src={two} dataspeed='2' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={three} dataspeed='-5' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={four} dataspeed='7' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={five} dataspeed='-3' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={six} dataspeed='5' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={seven} dataspeed='2' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={eight} dataspeed='-7' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={nine} dataspeed='3' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={ten} dataspeed='-5' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={eleven} dataspeed='2' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
            <img src={twelve} dataspeed='5' className='item' alt='bg-img' style={{transform: mSpeed}}></img>
                {/* <iframe width="560" height="315" src="https://www.youtube.com/embed/xcJtL7QggTI" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>

                <h1>Jonathon Tufts | Full Stack Developer</h1>
                <h5>Salt Lake City, Utah - App Academy</h5>
                <h4>Javascript - SQL - Postgresql - React.js - Express.js - Node.js - Python - Redux.js - Flask - HTML5 - CSS </h4> */}

        </div>
    );
}

export default NewSplash;
